            $response['response'] = $Row;
            $attr['customer_id'] = $Row['sell_to_cust_id'];
            $attr['chk_posting_grp'] = 1;
            $response['response']['customer_emails'] = $Row['c_emails'];
            $response['response']['c_emails'] = '';
            if ($attr['customer_id']){
