                                $Row['vatRange']['endRangeCode'] = $RowVat['endRangeCode'];
                            }
                        }
                    }
                    
                }

                    if ($Row['type'] == '0') {
                        $temp_attr['item_id'] = $Row['item_id'];
                        $temp_attr['order_id'] = $Row['order_id'];
                        $temp_attr['wh_id'] = $Row['warehouse_id'];
