            $response['volume'] = $volume;
            $response['volume_unit'] = $volume_unit;
            $response['weight'] = $weight;
            $response['weightunit'] = $weightunit;
            $response['weight_permission'] = $weight_permission;
            $response['volume_permission'] = $volume_permission;
            return $response;
