        function create_zip($files = array(),$destination = '',$overwrite = false) {
            // error_reporting(E_ALL) ;
            // ini_set('display_errors', '1');
            //if the zip file already exists and overwrite is false, return false
            if(file_exists($destination)) {
              
                    unlink($destination);

                }
            //vars
            $valid_files = array();
            //if files were passed in...
            if(is_array($files)) {
                //cycle through each file
                foreach($files as $file) {
                    //make sure the file exists
                    if(file_exists($file)) {
                        $valid_files[] = $file;
                    }
                }
            }
            //if we have good files...
            if(count($files)) {
                //create the archive
                $zip = new ZipArchive();
                if($zip->open($destination,$overwrite ? ZIPARCHIVE::OVERWRITE : ZIPARCHIVE::CREATE) !== true) {
                    return false;
                }
                //add the files
                // "http://localhost/silverowReporting/api/setup/invoice.pdf"


                foreach($files as $file) {
                    $existingFileNameArr = explode(".",basename($file)) ;
                    $newFileName = $existingFileNameArr[1].".". $existingFileNameArr[3];

                    // print_r($file);

                    $zip->addFromString($newFileName,file_get_contents($file));
                }
                //debug
                //echo 'The zip archive contains ',$zip->numFiles,' files with a status of ',$zip->status;
                
                //close the zip -- done!
                $zip->close();
                
                //check to make sure the file exists
                return $destination;
            }
            else
            {
                return false;
            }
        }

